package com.lsj.colaman.quickproject.test;

import android.annotation.SuppressLint;
import android.graphics.Bitmap;
import android.os.Environment;
import android.util.Base64;
import android.widget.ImageView;

import com.lsj.colaman.quickproject.R;
import com.lsj.colaman.quickproject.base.BaseActivity;
import com.lsj.colaman.quickproject.base.ImageLoader;
import com.lsj.colaman.quickproject.common.helper.PermissionUtil;
import com.yanzhenjie.permission.Permission;

import java.io.File;
import java.io.FileOutputStream;

import butterknife.BindView;
import io.reactivex.functions.Consumer;

import static android.os.Environment.DIRECTORY_PICTURES;

public class ImageActivity extends BaseActivity {


    @BindView(R.id.imageview)
    ImageView imageview;

    private String source = "";

    @Override
    protected int initLayoutRes() {
        return R.layout.activity_image;
    }

    @Override
    @SuppressLint("CheckResult")
    protected void initView() {
        PermissionUtil.getInstance().requestPermission(this, new PermissionUtil.RequestPermissionListener() {
            @Override
            public void requestSuccess() {
                File file = getCacheFile();
                saveBase64ToFile(source, file.getAbsolutePath());
            }

            @Override
            public void requestFail() {

            }
        }, Permission.WRITE_EXTERNAL_STORAGE);
        ImageLoader.getInstance().downLoadBitmap(this, "https://pic2.zhimg.com/80/v2-fbbb97b977b5cebc66dc3cefab0ac981_hd.jpg")
                .subscribe(new Consumer<Bitmap>() {
                    @Override
                    public void accept(Bitmap bitmap) throws Exception {
                    }
                });
    }

    private void saveBase64ToFile(String base64Code, String savePath) {
        byte[] buffer = Base64.decode(base64Code, Base64.DEFAULT);
        FileOutputStream out;
        try {
            out = new FileOutputStream(savePath);
            out.write(buffer);
            out.close();
        } catch (Exception e) {
            e.printStackTrace();
        }
    }

    private File getCacheFile() {
        return new File(Environment.getExternalStoragePublicDirectory(DIRECTORY_PICTURES), System.currentTimeMillis() + ".jpg");
    }

}
